import React, { useState, useEffect } from 'react'
import { useParams } from 'react-router-dom'
import articleContent from './article-content'

//page
import NotFound from './NotFound'

//components
import Articles from '../components/Articles'


const Article = () => {
  const {name} = useParams();
  const article = articleContent.find((article) => article.name === name);

  const [articleInfo, setArticleInfo] = useState({ comments: [] });

  //so since the code that we are using to use to fetch the data involves a synchronised fetching operation which will be using asyn and await keyword inside the useeffect // the only prob here is that useeffect does not allow the func that we pass here to be async so suppose if we do useEffect()
  useEffect(() => {
    console.log("Component Mounted");
  }); //this use effect run everytime when component loads
  



  if(!article) 
    return <NotFound />
    //<h1>Article does not exist</h1> 
  const otherArticles = articleContent.filter(article => article.name !== name)
  return (
    <>
      {/* <h1 className='sm:text-4xl text-2xl font-bold my-6 text-gray-900'>This is the {name} Article</h1>   
      {/* so whetever param you will pass in url i will show here {name}
      http://localhost:5173/article/learn-react then it will show This is the learn-react Article*/} 

      <h1 className='sm:text-4xl text-2xl font-bold my-6 text-gray-900'>{article.title}</h1> 
      {article.content.map((paragraph, index) => (
        <p className="mx-auto leading-relaxed text-base mb-4" key={index}>{paragraph}</p>
      ))}
       
      <h1 className='sm:text-2xl text-xl font-bold my-4 text-gray-900'>
        Other Articles
      </h1>

      <div className='flex flex-wrap -m-4'>
        <Articles articles= {otherArticles} /> 
        {/* passing it as prop otherArticles */}
      </div>

    </>
    // use react fragment <></> instead of <div></div> it is always better
  )
}

export default Article
