import React, { useState, useEffect } from 'react'
import { useParams } from 'react-router-dom'
import articleContent from './article-content'

//page
import NotFound from './NotFound'

//components
import Articles from '../components/Articles'

import CommentsList from '../components/CommentsList'

import AddCommentForm from '../components/AddCommentForm'


const Article = () => {
  const {name} = useParams();
  const article = articleContent.find((article) => article.name === name);

  const [articleInfo, setArticleInfo] = useState({ comments: [] });

 
  useEffect(() => {
    const fetchData = async () => {
      const result = await fetch(`/api/articles/${name}`);
      const body = await result.json();
      console.log(body);
      setArticleInfo(body);
    };
    fetchData();
    
  }, [name]);
  



  if(!article) 
    return <NotFound />
     
  const otherArticles = articleContent.filter(article => article.name !== name);
  return (
    <>
    
      <h1 className='sm:text-4xl text-2xl font-bold my-6 text-gray-900'>{article.title}</h1> 
      {article.content.map((paragraph, index) => (
        <p className="mx-auto leading-relaxed text-base mb-4" key={index}>{paragraph}</p>
      ))}

      <CommentsList comments={articleInfo.comments} />
      <AddCommentForm articleName={name} setArticleInfo={setArticleInfo} />

       
      <h1 className='sm:text-2xl text-xl font-bold my-4 text-gray-900'>
        Other Articles
      </h1>

      <div className='flex flex-wrap -m-4'>
        <Articles articles= {otherArticles} /> 
        
      </div>





      {articles.map((article, index) => (
        <div key={index} className='p-4 md:w-1/2'>
          <div className="h-full border-2 corder-gray-200 border-opacity-60 rounded-lg overflow-hidden">
            <Link to={`/article/${article.name}`}>
              <img className="lg:h-48 md:h-36 w-full object-cover object-center" src={article.thumbnail} alt="blog" />
            </Link>
            <div className='p-6'>
              <Link to={`/article/${article.name}`}>
                <h3 className='text-lg font-medium text-gray-900 mb-3'>
                  {article.title}
                </h3>
              </Link>
              <div className='leading-relaxed mb-3' dangerouslySetInnerHTML={{ __html: article.content.join('<br><br>') }} />
              <div className='flex item-center flex-wrap'>
                <Link className='text-indigo-500 inline-flex items-center md:mb-2 lg:mb-0' to={`/article/${article.name}`}>
                  Learn more
                </Link>
              </div>
            </div>
          </div>
        </div>
      ))}
    </>
   
  );
};

export default Article
